import React from 'react';
import Grid from '@material-ui/core/Grid';
import { isMobile } from 'react-device-detect';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Styles from '../../Styles/PersonInfo';
import Modal from 'react-bootstrap/Modal'

export default class PersonInfo extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            'blAsk': true
        };
    }

    updateState = state => this.setState(state)

    render() {
        const { data, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        const Inner = Object.entries(data).map((entry, idx) => {
            return (
                <Grid
                    container
                    justify="center"
                    key={idx}
                >
                    <Grid
                        item
                        xs={12}
                        sm={12}
                        md={4}
                        lg={4}
                        className={isMobile ? "text-center" : "text-right"}
                        style={Styles.cardBodyRowCol1}
                    >
                        {Translations[entry[0]]}
                    </Grid>
                    <Grid
                        item
                        xs={12}
                        sm={12}
                        md={8}
                        lg={8}
                        style={Styles.cardBodyRowCol2}
                    >
                        {entry[1]}
                    </Grid>
                </Grid>
            )
        })
        return(
            <>
                <Row
                    style={Styles.mainRow}
                >
                    <Col xs sm md lg>
                        <Card
                            bg="light"
                            key="1"
                            text="dark"
                        >
                            <Card.Header style={Styles.cardHeader}>
                                {Translations.main_title_trans}
                            </Card.Header>
                            <Card.Body style={Styles.cardBody}>
                                {Inner}
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
                <InfoAsk
                    updatePersonInfoState={this.updateState}
                    blAsk={this.state.blAsk}
                />
            </>
        )
    }

}


const InfoAsk = props => {
    return (
        <Modal
            size="lg"
            show={props.blAsk}
            onHide={() => props.updatePersonInfoState({
                'blAsk': false
            })}
            aria-labelledby="example-modal-sizes-title-lg"
        >
            <Modal.Header
                closeButton={true}
            >
                <Modal.Title id="example-modal-sizes-title-lg">
                    Large Modal
                    </Modal.Title>
            </Modal.Header>
            <Modal.Body>...</Modal.Body>
        </Modal>
    )
}
