import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import el from '../../Languages/el.json';
import en from '../../Languages/en.json';
import ka from '../../Languages/ka.json';

export default class LanguageSelector extends React.Component {

    render() {
        const Translations = this.props.lngTranslations[this.constructor.name];
        const { lng, updateAppState } = this.props;
        return(
            <Row
                style={{
                    textAlign: 'right'
                }}
            >
                <Col
                    xs={12} sm={12} md={12} lg={12}
                >
                    <Dropdown
                        onSelect={lngNew => {
                            if (lngNew !== lng) {
                                if (lngNew === "en") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: enTrans
                                    });
                                } else if (lngNew === "ka") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: kaTrans
                                    });
                                } else {
                                    updateAppState({
                                        lng: "el",
                                        lngTranslations: elTrans
                                    });
                                }
                            }
                        }}
                    >
                        <Dropdown.Toggle
                            variant="secondary"
                            id="language_select"
                        >
                            {Translations.dropdown_choose_language}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item
                                active={lng === "el"}
                                eventKey="el"
                            >
                                {Translations.dropdown_choose_language_el}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "en"}
                                eventKey="en"
                            >
                                {Translations.dropdown_choose_language_en}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "ka"}
                                eventKey="ka"
                            >
                                {Translations.dropdown_choose_language_ka}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>
            </Row>
        )
    }

}
