import React from 'react';
import Grid from '@material-ui/core/Grid';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import el from '../../Languages/el.json';
import en from '../../Languages/en.json';
import ka from '../../Languages/ka.json';
import Styles from '../../Styles/LanguageSelector/';

export default class LanguageSelector extends React.Component {

    render() {
        const { lng, updateAppState, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        return(
            <Grid
                container
                justify="center"
            >
                <Dropdown
                        size="sm"
                        onSelect={lngNew => {
                            if (lngNew !== lng) {
                                if (lngNew === "en") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: en
                                    });
                                } else if (lngNew === "ka") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: ka
                                    });
                                } else {
                                    updateAppState({
                                        lng: "el",
                                        lngTranslations: el
                                    });
                                }
                            }
                        }}
                    >
                        <Dropdown.Toggle
                            variant="secondary"
                            id="language_select"
                            ref={ref => this.lngSelectRef = ref}
                        >
                            {Translations.dropdown_choose_language_trans}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item
                                active={lng === "el"}
                                eventKey="el"
                            >
                                {Translations.dropdown_choose_language_el_trans}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "en"}
                                eventKey="en"
                            >
                                {Translations.dropdown_choose_language_en_trans}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "ka"}
                                eventKey="ka"
                            >
                                {Translations.dropdown_choose_language_ka_trans}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
            </Grid>
            // <Row
            //     style={Styles.mainRow}
            // >
            //     <Col
            //         xs={8}
            //         sm={8}
            //         md={8}
            //         lg={8}
            //         style={Styles.col1}
            //     >
            //         <h1 style={Styles.mainTitle}>
            //             {Translations.main_title_trans}
            //         </h1>
            //     </Col>
            //     <Col
            //         xs={4}
            //         sm={4}
            //         md={4}
            //         lg={4}
            //         style={Styles.col2}
            //     >

            //     </Col>
            // </Row>
        )
    }

}
