import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import data from '../../Resources/CVData/data.json';
import el from '../../Languages/el.json';
import LanguageSelector from '../../Components/LanguageSelector';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            lng: "en",
            lngTranslations: el
        }
    }

    updateState = state => this.setState(state);

    render() {
        return(
            <Container
                fluid="xl"
                style={{
                    textAlign: 'center',
                    padding: '2em',
                    marginTop: '1em',
                    border: '0.1em #888 solid',
                    boxShadow: '15px 15px 15px -1px #888'
                }}
            >
                <LanguageSelector
                    updateAppState={this.updateState}
                    lng={this.state.lng}
                    lngTranslations={this.state.lngTranslations}
                />
                <Row
                    className="justify-content-md-center"
                >
                    <Col
                        xs={12} sm={12} md={12} lg={12}
                    >
                    </Col>
                </Row>
            </Container>
        )
    }

}
