import React from 'react';
import Container from 'react-bootstrap/Container';
import Styles from '../../Styles/App';
import data from '../../Resources/CVData/data.json';
import el from '../../Languages/el.json';
import LanguageSelector from '../../Components/LanguageSelector';
import PersonInfo from '../../Components/PersonInfo';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            lng: "el",
            lngTranslations: el
        }
    }

    updateState = state => this.setState(state);

    render() {
        const Inner  = data.cv_info.map(sData => {
            console.log(sData);
        });
        return(
            <Container
                fluid="xl"
                style={Styles.mainContainer}
            >
                <LanguageSelector
                    updateAppState={this.updateState}
                    lng={this.state.lng}
                    lngTranslations={this.state.lngTranslations}
                />
                <PersonInfo
                    data={data.person_info}
                    lngTranslations={this.state.lngTranslations}
                />
                {Inner}
            </Container>
        )
    }

}
