import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Styles from '../../Styles/App';
import { data as CVData } from '../../Resources/CVData/data.json';
import el from '../../Languages/el.json';
import LanguageSelector from '../../Components/LanguageSelector';
import ListGroup from 'react-bootstrap/ListGroup';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            lng: "el",
            lngTranslations: el
        }
    }

    updateState = state => this.setState(state);

    render() {
        return(
            <Container
                fluid="xl"
                style={Styles.mainContainer}
            >
                <LanguageSelector
                    updateAppState={this.updateState}
                    lng={this.state.lng}
                    lngTranslations={this.state.lngTranslations}
                />
                <Row
                    className="justify-content-md-center"
                    style={Styles.mainRow}
                >
                    <Col xs sm md lg>
                        <Card
                            bg="light"
                            key="1"
                            text="dark"
                        >
                            <Card.Header

                                style={{
                                fontSize: '1.5em'
                            }}>
                                Header
                            </Card.Header>
                            <Card.Body>
                                <ListGroup horizontal="sm" key={1}>
                                    <ListGroup.Item>This ListGroup</ListGroup.Item>
                                    <ListGroup.Item>renders horizontally</ListGroup.Item>
                                    <ListGroup.Item>on {breakpoint}</ListGroup.Item>
                                    <ListGroup.Item>and above!</ListGroup.Item>
                                </ListGroup>
                            </Card.Body>
                        </Card>


                    </Col>
                </Row>
            </Container>
        )
    }

}
