import React from 'react';
import Grid from '@material-ui/core/Grid';
import Styles from '../../Styles/App';
import data from '../../Resources/CVData/data.json';
import el from '../../Languages/el.json';
import LanguageSelector from '../../Components/LanguageSelector';
import PersonInfo from '../../Components/PersonInfo';
import TimelineView from '../TimelineView';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            lng: "el",
            lngTranslations: el
        }
    }

    updateState = state => this.setState(state);

    render() {
        const { lng, lngTranslations } = this.state;
        const Inner  = data.cv_info.map((info, idx) => {
            if (info.type === "timeline") {
                return (
                    <TimelineView
                        key={idx}
                        data={info}
                        lng={lng}
                        lngTranslations={lngTranslations}
                    />
                )
            } else {
                return (<></>)
            }
        });
        return(
            <Grid
                container
                justify="center">
                <Grid
                    item xs={12}
                    sm={12}
                    md={6}
                    lg={8}
                    style={Styles.mainContainer}
                >
                    <LanguageSelector
                        updateAppState={this.updateState}
                        lng={lng}
                        lngTranslations={lngTranslations}
                    />
                    <PersonInfo
                        data={data.person_info}
                        lngTranslations={lngTranslations}
                    />
                    {Inner}
                </Grid>
            </Grid>
        )
    }

}
