import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import el from '../../Languages/el.json';
import en from '../../Languages/en.json';
import ka from '../../Languages/ka.json';

export default class LanguageSelector extends React.Component {

    render() {
        const { lng, updateAppState, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        return(
            <Row
                style={Styles.mainRow}
            >
                <Col
                    xs sm md lg
                >
                    <Dropdown
                        size="sm"
                        onSelect={lngNew => {
                            if (lngNew !== lng) {
                                if (lngNew === "en") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: en
                                    });
                                } else if (lngNew === "ka") {
                                    updateAppState({
                                        lng: lngNew,
                                        lngTranslations: ka
                                    });
                                } else {
                                    updateAppState({
                                        lng: "el",
                                        lngTranslations: el
                                    });
                                }
                            }
                        }}
                    >
                        <Dropdown.Toggle
                            variant="secondary"
                            id="language_select"
                            ref={ref => this.lngSelectRef = ref}
                        >
                            {Translations.dropdown_choose_language}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item
                                active={lng === "el"}
                                eventKey="el"
                            >
                                {Translations.dropdown_choose_language_el}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "en"}
                                eventKey="en"
                            >
                                {Translations.dropdown_choose_language_en}
                            </Dropdown.Item>
                            <Dropdown.Item
                                active={lng === "ka"}
                                eventKey="ka"
                            >
                                {Translations.dropdown_choose_language_ka}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>
            </Row>
        )
    }

}
