import React from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import data from '../../Resources/CVData/data.json'
import { el as lngEl } from '../../Languages/el.json'
import { en as lngEn } from '../../Languages/en.json'
import { ka as lngKa } from '../../Languages/ka.json'
import LanguageSelector from '../../Components/LanguageSelector';

import Button from 'react-bootstrap/Button'

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            'lng': 0,
            'language': {}
        }
    }

    componentDidMount = () => {
        console.log(`component did mount  ${this.state.lng}`);
    }

    componentDidUpdate = () => {
        console.log(`component did mount  ${this.state.lng}`);
    }

    updateState = state => this.setState(state);

    render() {
        return(
            <Container
                fluid="xl"
                style={{
                    textAlign: 'center',
                    padding: '2em',
                    marginTop: '1em',
                    border: '0.1em #888 solid',
                    boxShadow: '15px 15px 15px -1px #888'
                }}
            >
                <Row
                    className="justify-content-md-center"
                >
                    <Col
                        xs={12} sm={12} md={12} lg={12}
                    >


                        <Button variant="primary" onClick={() => {
                            this.updateState({
                                'lng': Math.random()
                            });
                        }}>
                            Small button
                        </Button>



                    </Col>
                </Row>
            </Container>
        )
    }

}
