import React from 'react';
import Grid from '@material-ui/core/Grid';
import Card from 'react-bootstrap/Card';
import Timeline from '@material-ui/lab/Timeline';
import TimelineItem from '@material-ui/lab/TimelineItem';
import TimelineSeparator from '@material-ui/lab/TimelineSeparator';
import TimelineConnector from '@material-ui/lab/TimelineConnector';
import TimelineContent from '@material-ui/lab/TimelineContent';
import TimelineOppositeContent from '@material-ui/lab/TimelineOppositeContent';
import TimelineDot from '@material-ui/lab/TimelineDot';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import SchoolOutlinedIcon from '@material-ui/icons/SchoolOutlined';
import Moment from 'moment';
import Styles from '../../Styles/TimelineView'

export default class TimelineView extends React.Component {

    render() {
        const { data, lng, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        const Inner = data.data.map((sTimeline, idx) => {
            return (
                <TimelineItem
                    key={idx}
                >
                    <TimelineOppositeContent style={Styles.timelineOppositeContent}>
                        <Typography
                            variant="body2"
                            color="textSecondary"
                            style={Styles.timelineOppositeContentTypo}
                        >
                            {sTimeline.date.replace(/{_UNTIL_NOW_YEAR_TRANS_}/g, Moment(new Date()).format('Y'))}
                        </Typography>
                    </TimelineOppositeContent>
                    <TimelineSeparator>
                        <TimelineDot>
                            <SchoolOutlinedIcon color="primary" />
                        </TimelineDot>
                        <TimelineConnector />
                    </TimelineSeparator>
                    <TimelineContent>
                        <Paper elevation={3} style={Styles.timelineContentPaper}>
                            <Typography>
                                {sTimeline.content[lng]}
                            </Typography>
                        </Paper>
                    </TimelineContent>
                </TimelineItem>
            )
        });
        return (
            <Grid
                container
                justify="center"
                style={Styles.mainRow}
            >
                <Grid
                    item
                    xs={12}
                    sm={12}
                    md={12}
                    lg={12}
                >
                    <Card
                        bg="light"
                        key="1"
                        text="dark"
                    >
                        <Card.Header style={Styles.cardHeader}>
                            {Translations[data.title]}
                        </Card.Header>
                        <Card.Body style={Styles.cardBody}>
                            <Timeline align="alternate">
                                {Inner}
                            </Timeline>
                        </Card.Body>
                    </Card>
                </Grid>
            </Grid>
        )
    }

}
