import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import { el as lngEl } from '../../Languages/el.json';
import { en as lngEn } from '../../Languages/en.json';
import { ka as lngKa } from '../../Languages/ka.json';

export default class LanguageSelector extends React.Component {

    render() {
        const Translations = this.props.lngTranslations[this.constructor.name];
        return(
            <Row
                style={{
                    textAlign: 'right'
                }}
            >
                <Col
                    xs={12} sm={12} md={12} lg={12}
                >
                    <Dropdown
                        onSelect={data => {
                            console.log(data);
                        }}
                    >
                        <Dropdown.Toggle
                            variant="secondary"
                            id="language_select"
                        >
                            {Translations.dropdown_choose_language}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item
                                active
                                href="el"
                            >
                                {Translations.dropdown_choose_language_el}
                            </Dropdown.Item>
                            <Dropdown.Item
                                href="en"
                            >
                                {Translations.dropdown_choose_language_en}
                            </Dropdown.Item>
                            <Dropdown.Item
                                href="ka"
                            >
                                {Translations.dropdown_choose_language_ka}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>
            </Row>
        )
    }

}
