import React from 'react';
import Container from 'react-bootstrap/Container';
import Styles from '../../Styles/App';
import data from '../../Resources/CVData/data.json';
import el from '../../Languages/el.json';
import LanguageSelector from '../../Components/LanguageSelector';
import PersonInfo from '../../Components/PersonInfo';
import TimelineView from '../TimelineView';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            lng: "el",
            lngTranslations: el
        }
    }

    updateState = state => this.setState(state);

    render() {
        const { lng, lngTranslations } = this.state;
        const Inner  = data.cv_info.map((info, idx) => {
            if (info.type === "timeline") {
                return (
                    <TimelineView
                        key={idx}
                        data={info}
                        lng={lng}
                        lngTranslations={lngTranslations}
                    />
                )
            } else {
                return (<></>)
            }
        });
        return(
            <div className="col-12" style={Styles.mainContainer}>
                <div className="row">
                    <div className="col-xs-12 col-sm-12 col-md-12 col-l-12">

                    </div>
