import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Styles from '../../Styles/PersonInfo';

export default class PersonInfo extends React.Component {

    render() {
        const { data, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        const inner = Object.entries(data).map((entry, idx) => {
            let key = entry[0];
            let val = entry[1];
            return (
                <Row key={idx}>
                    <Col
                        xs={12}
                        sm={12}
                        md={6}
                        lg={6}
                        className="d-flex justify-content-xs-center justify-content-sm-center justify-content-md-start justify-content-lg-start"
                        style={Styles.cardBodyRowCol1}
                    >
                        {Translations[`${key}_trans`]}
                    </Col>
                    <Col
                        xs={12}
                        sm={12}
                        md={6}
                        lg={6}
                        className=""
                        style={Styles.cardBodyRowCol2}>
                        bbssdd
                    </Col>
                </Row>
            )
        })
        return(
            <Row
                style={Styles.mainRow}
            >
                <Col xs sm md lg>
                    <Card
                        bg="light"
                        key="1"
                        text="dark"
                    >
                        <Card.Header style={Styles.cardHeader}>
                            Header
                        </Card.Header>
                        <Card.Body style={Styles.cardBody}>
                            <Container fluid>
                                {inner}
                            </Container>

                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        )
    }

}
