import React from 'react';
import Grid from '@material-ui/core/Grid';
import { isMobile } from 'react-device-detect';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Styles from '../../Styles/PersonInfo';

export default class PersonInfo extends React.Component {

    render() {
        console.log(isMobile);
        const { data, lngTranslations } = this.props;
        const Translations = lngTranslations[this.constructor.name];
        const Inner = Object.entries(data).map((entry, idx) => {
            return (
                <>
                    <Grid
                        key={idx}
                        item
                        xs={12}
                        sm={12}
                        md={4}
                        lg={4}
                        className={isMobile ? "text-center" : "text-left"}
                        style={Styles.cardBodyRowCol1}
                    >
                        {Translations[entry[0]]}
                    </Grid>
                    <Grid
                        key={idx+2}
                        item
                        xs={12}
                        sm={12}
                        md={8}
                        lg={8}
                        style={Styles.cardBodyRowCol2}
                    >
                        {entry[1]}
                    </Grid>
                </>
            )
        })
        return(
            <Row
                style={Styles.mainRow}
            >
                <Col xs sm md lg>
                    <Card
                        bg="light"
                        key="1"
                        text="dark"
                    >
                        <Card.Header style={Styles.cardHeader}>
                            {Translations.main_title_trans}
                        </Card.Header>
                        <Card.Body style={Styles.cardBody}>
                            <Grid
                                container
                                justify="center"
                            >
                                {Inner}
                            </Grid>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        )
    }

}
